import React from "react";
import ReactDOM  from "react-dom";
import { responsive } from "./responsive.js";
import cppimg from './cpp.png'
import javaimg from './java.png'
import reactimg from './react.png'
import dsimg from './ds.png'
import algoimg from './algorithms.png'
import jsimg from './js.png'
import osimg from './Os.png'
import dbmsimg from './Dbms.png'
let arr=[[],['C++',cppimg,0],['Java',javaimg,1],['React',reactimg,2],['Data\nStructures',dsimg,3],['Algorithms',algoimg,4],['JavaScript',jsimg,5],['Operating\nSystems',osimg,6]
,['DBMS',dbmsimg,7],[]]
export class Scroll_header extends React.Component{
    constructor(){
        super()
        this.state={index:1,style:responsive.scroll_bar.Main()}
        this.resize=this.resize.bind(this)
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState({style:responsive.scroll_bar.Main()})
    }
    handleleftclick(){
        if(this.state.index==0)return;
        this.setState((prev)=>{return {index:prev.index-1,style:prev.style}});
        return;
    }
    handlerightclick(){
        if(this.state.index>=arr.length-2)return;
        this.setState((prev)=>{return {index:prev.index+1,style:prev.style}});
        return;

    }
    render(){
        return (
            <div style={this.state.style}>
                <Scroll img={arr[this.state.index-1][1]} h1={arr[this.state.index-1][0]} ind={arr[this.state.index-1][2]}/>
                <Scroll img={arr[this.state.index][1]} h1={arr[this.state.index][0]} ind={arr[this.state.index][2]}/>
                <Scroll img={arr[this.state.index+1][1]} h1={arr[this.state.index+1][0]} ind={arr[this.state.index+1][2]}/>
            </div>
        )
    }
}

class Scroll extends React.Component{
    constructor(props){
        super(props)
        let flag=0;
        if(this.props.ind==1)flag=1;
        this.state={style:responsive.scroll_bar.Element(flag),image:responsive.scroll_bar.image(flag,this.props.img),text:responsive.scroll_bar.text(flag)};
    }
    resize(){
        this.setState({style:responsive.scroll_bar.Element(0),image:responsive.scroll_bar.image(0,this.props.img),text:responsive.explore.text()})
    }
    render(){
        return(
            <div style={this.state.style}>
                <div style={this.state.image}>
                </div>

            </div>
        )
    }
}
