import ReactDOM from 'react-dom'
import React from 'react'
import './index.css'
class News extends React.Component{
  constructor(){
    super()
    this.state={page:1}
  }
  shouldComponentUpdate(prevprops,nextstate){
   return nextstate.page!=this.state.page
  }
  render(){
    return(
      <div>
      <div className='Heading'>
      <div className='newslogo'></div>
      <div className='newsheading'>NEWS24</div>
      </div>
      <hr/>
      <Headlines page={this.state.page}/>
      <div className='Pagenation'>
        {this.state.page>1 && <button onClick={()=>this.setState((prev)=>{return {page:prev.page-1}})}> {"<"} </button>}
        <button onClick={(prev)=>this.setState((prev)=>{return {page:prev.page+1}})}>{">"}</button>
      </div>
      </div>

    )
  }
}
class Headlines extends React.Component{
  constructor(props){
    super(props)
    this.state={headlines:[]}
  }
  componentDidMount(){
    fetch(`https://newsapi.org/v2/everything?q=technology&page=${this.props.page}&pageSize=10&apiKey=9ed751fd08904a52a2ba4593563fe7d4`).then((res)=>res.json()).then((res)=>this.setState({headlines:res.articles}));
  }
  render(){
    return(
      <div>
        {this.state.headlines.length>0 &&
        this.state.headlines.map((news)=><><div className='headlines'><img className='newsimg' src={news.urlToImage}></img><a href={news.url} className='newstext'>{news.title}<br></br><br></br><br></br>source::{news.source.name}</a></div><br></br></>)}
      </div>
    )
  }
}

ReactDOM.render(<News/>,document.getElementById('root'))
