import React from 'react'
import ReactDOM from 'react-dom'
import { Heading } from './heading.js'
import { Mocktest_page } from './Mocktest.js'
import { responsive_mock } from './responsive.js'
export class Test_launch extends React.Component{
    constructor(props){
        super(props)
        this.state={
            style:responsive_mock.test_page.instructions(),button:responsive_mock.test_page.button()
        }
    }
    render(){
        return <div style={this.state.style}>
            <div >
                <h1 style={{color:'white'}}> {"Instructions"}</h1>

            </div>
            <button style={this.state.button}>
                Start
            </button>
        </div>
    }
}
export class Test_page extends React.Component{
    constructor(props){
        super(props)
        this.state={index:0,time:10,timer:responsive_mock.test_page.timer(),question:responsive_mock.test_page.question(),tab:responsive_mock.test_page.tab()
        ,question_bar:responsive_mock.test_page.question_bar(),option_bar:responsive_mock.test_page.options.main(),options:responsive_mock.test_page.options.option(),
    end:responsive_mock.test_page.end(),question_text:responsive_mock.test_page.question_text()}
        this.resize=this.resize.bind(this)
        this.handleend=this.handleend.bind(this)
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState((prev)=>{return {index:prev.index,time:prev.time,timer:responsive_mock.test_page.timer(),question:responsive_mock.test_page.question(),tab:responsive_mock.test_page.tab()
            ,question_bar:responsive_mock.test_page.question_bar(),option_bar:responsive_mock.test_page.options.main(),options:responsive_mock.test_page.options.option(),question_text:responsive_mock.test_page.question_text()}})
    }
    componentDidMount(){
        this.starttimer()
    }
    starttimer(){
        if(this.state.index==this.props.arr.length){this.handleend();return;}
        if(this.state.time==0){
            if(this.state.index==this.props.arr.length){this.handleend();return;}
            this.setState((prev)=>{return {index:prev.index+1,time:10,timer:prev.timer}},this.starttimer);
            return;
        }
        setTimeout(()=>
        {
        this.setState((prev)=>{return {index:prev.index,time:prev.time-1,timer:prev.timer}},this.starttimer)},1000);
    }
    handleend(){
        ReactDOM.render(<Mocktest_page/>,document.getElementById('root'))
    }
    render(){
        return<div>
            <div style={this.state.question_bar}>
            {this.props.arr.map((ele,ind)=><div style={this.state.tab}>{ind+1}</div>)}
            </div>
            <div style={this.state.timer}>{'00:'+this.state.time}</div>
            <button style={this.state.end} onClick={this.handleend}>End Test</button>
            <div style={{position:'relative',display:'flex'}}>
            <div style={this.state.question}><textarea style={this.state.question_text}>{this.props.arr[this.state.index].question}</textarea></div>
            <div style={this.state.option_bar}>
                <div style={this.state.options}>
                    <input type="checkbox" id='1'></input>
                    <label htmlFor='1' >{this.props.arr[this.state.index].option_01}</label>
                </div>
                <hr style={{border:'1px solid grey'}}/>
                <div style={this.state.options}>
                    <input type="checkbox" id='2'></input>
                    <label htmlFor='2' >{this.props.arr[this.state.index].option_02}</label>
                </div>
                <hr style={{border:'1px solid grey'}}/>
                <div style={this.state.options}>
                    <input type="checkbox" id='3'></input>
                    <label htmlFor='3' >{this.props.arr[this.state.index].option_03}</label>
                </div>
                <hr style={{border:'1px solid grey'}}/>
                <div style={this.state.options}>
                    <input type="checkbox" id='4'></input>
                    <label htmlFor='4' >{this.props.arr[this.state.index].option_04}</label>
                </div>
                <hr style={{border:'1px solid grey'}}/>
            </div>
            </div>
        </div>
    }
}
