import React from "react";
import ReactDOM from "react-dom";
import { responsive } from "./responsive.js";
import {Heading} from './heading.js'

export class Questions extends React.Component{
    constructor(props){
        super(props)
        this.state={sidebar_style:responsive.sidebar_02.main(),index:0,image:responsive.questions.image(this.props.obj.image),heading:responsive.questions.heading()}
        this.handleclick=this.handleclick.bind(this)
    }
    handleclick(){
        //this.setState({index:0})
    }
    render(){
        return(
            <div>
            <Heading/>
            <div style={this.state.image}>
            </div>
            <div style={this.state.heading}>
                {this.props.obj.h1}
            </div>
            <div style={this.state.sidebar_style}>
                {
            this.props.obj.subtopics.map((ele)=>{
            if(this.state.index!=ele[2])
           return <Subtopic func={this.props.func} img={ele[1]} topic={ele[0]} index={ele[2]} flag={false}/>
            else
           return <Subtopic func={this.props.func} img={ele[1]} topic={ele[0]} index={ele[2]} flag={true}/>
        })
                }
            </div>           
         </div>
        )
    }

}
class Subtopic extends React.Component{
    constructor(props){
        super(props)
        this.state={style:responsive.sidebar_02.Element(this.props.flag),text:responsive.sidebar_02.text(),image:responsive.sidebar_02.image(this.props.img)}
        this.resize=this.resize.bind(this);
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState({style:responsive.sidebar.Element(),text:responsive.sidebar.text(),image:responsive.sidebar.image(this.props.img)});
    }
    render(){
        return(
            <div style={this.state.style} onClick={this.props.func}>
            <div style={this.state.image}>

            </div>
              <text style={this.state.text}>
                            {this.props.topic}
              </text>
        </div>
        )
    }
}
