import React from 'react'
import ReactDOM from 'react-dom';
import './main.css'
let use={
  username:"",
  password:""
};
class Login extends React.Component{
  constructor(){
    super()
    this.state={text:""};
    this.update=this.update.bind(this);
    this.show=this.show.bind(this);
    this.verify=this.verify.bind(this);
  }

  update(e){
    this.setState({text:e.target.value})
    use.username=e.target.value;
  }
  show(){
    let data={
      method:'POST',
      body:JSON.stringify(use),
      headers: {'Content-Type':'application/json'},
    }
     fetch("http://localhost:8000/post",data)


  }
  verify(){
    let flag=0;
  function t(callback){
  fetch("http://localhost:8000/get").then((res)=>res.json()).then((res)=>{
      res.map((data)=>{
        if(data.username==use.username && data.password==use.password)
        {alert(1);flag=1;}
      })})
      callback();
    }
    function c(){
    if(flag==0)alert("false")
    else
    alert("true");
    }
    t(c)

  }
  render(){
    return(
    <div className='Login' >
      <lable htmlFor="Username">Username</lable>
      <input className='Username' onKeyUp={this.update}/><br></br>
      <br></br>
      <Password/>
      <br></br><br></br>
      <button onClick={this.verify} style={{fontSize:'20px'}}>Login</button>
      <br></br><br></br>
      <button onClick={this.show} style={{fontSize:'20px'}}>Sign Up</button>

    </div>)
  }
}
class Password extends React.Component{
  constructor(){
    super()
    this.state={text:''};
    this.update=this.update.bind(this);
  }
  update(e){
    this.setState({text:e.target.value});
    use.password=e.target.value;
  }
  render(){
    return(<>
      <label htmlFor='Password'>Password</label>
      <input className='Password' onKeyUp={this.update} />
      </>
    )
  }
}
ReactDOM.render(<Login/>,document.getElementById('root'));
