import createSudoku  from './App.js'
import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'
let ans=createSudoku()
let sudoku=JSON.parse(JSON.stringify(ans));
let i=0;
while(i<30)
  {
    let row=Math.floor(Math.random()*9)
    let col=Math.floor(Math.random()*9)
    if(sudoku[row][col]!==-1)
    {
      i++;
      sudoku[row][col]=-1;
    }
  }
let count=0;
let temp=JSON.parse(JSON.stringify(sudoku));
const root=ReactDOM.createRoot(document.getElementById('root'));
class Start extends React.Component{
  func=()=>{
    root.render(<Sudoku />)
  }
  render(){
    return(
      <button className='startButton' onClick={this.func}>
        Click to Start
      </button>
    )
  }
}
class Sudoku extends React.Component{
  ans=()=>{
      count=0;
      for(let i=0;i<sudoku.length;i++)
      {
        for(let j=0;j<sudoku[i].length;j++)
        {
          if(sudoku[i][j]==-1)
          {
            let t=document.getElementById(count);
            t.value=ans[i][j]
            count++;
          }
        }
      }
  }
  verify=()=>{
    count=0;
    let flag=true;
    for(let i=0;i<sudoku.length;i++)
    {
      for(let j=0;j<sudoku[i].length;j++)
      {
        if(sudoku[i][j]==-1)
        {
          let t=document.getElementById(count).value;
          count++;
          if(t!=ans[i][j])
          {alert("try again!"); flag=false
          break;}
        }
      }
      if(!flag)break;
    }
    if(flag)
    alert("congragulations!")
  }
  render(){
    return (<>
    <div className='Sudoku'>
      {sudoku.map((arr,index)=><Row arr={arr} r={index}/>)}
      </div>
      <div >
        <button className='ansButton' onClick={this.ans}>check answer</button>
      </div>

      <div >
        <button className='verifyButton'onClick={this.verify}>submit</button>
      </div>
      </>
    )
  }
}
class Row extends React.Component{
  constructor(props)
  {
    super(props)
  }
  render()
  {
    return(
      <div className='row'>{
        this.props.arr.map((val,index)=>{
        if(val==-1)
        return <Input r={this.props.r} c={index} />
        else
        return <Element val={val}/>
      })
    }
    </div>
    )
  }
}
class Input extends React.Component{
  constructor(props)
  {
    super(props)

  }
  update=(e)=>{
    let k=e.target.value;
    if(k.length>1 || k.charCodeAt('0')-'0'.charCodeAt('0')>9)
    {alert("invalid number enter between (0-9)");
    e.target.value=""}

  }

  render(){
    return  <input id={count++}className="Element1"type="text" onKeyUp={this.update}/>
  }
}
class Element extends React.Component{
  constructor(props)
  {
    super(props)
  }
  render(){
   return( <div className='Element'>
      {this.props.val}
    </div>)
  }
}
root.render(<Start/>)
let data={
  name:'alex',
  id:1,
  task:'for demo'
}
const requestOptions = {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body:JSON.stringify(data)
};
//fetch('http://localhost:8000', requestOptions);
let d
let res=await fetch('http://localhost:8000/get')
.then((res)=>d=res.json())
.then((data)=>d=data)
d[0].id=10
fetch('http://localhost:8000',{
  method:'POST',
  headers:{'Content-Type':'application/json'},
  body:JSON.stringify(d)
})
