import ReactDOM from 'react-dom'
import React from 'react'
import './index.css'
let addres;

class News extends React.Component{
  constructor(){
    super()
    this.state={page:1}
  }

  render(){
    return(
      <div>
      <div className='Heading'>
      <div className='newslogo'></div>
      <div className='newsheading'>NEWS24</div>
      </div>
      <hr/>
      <Headlines page={this.state.page}/>
      <div className='Pagenation'>
        {this.state.page>1 && <button onClick={()=>addres.util()}> {"<"} </button>}
        <button onClick={()=>addres.util()}>{">"}</button>
      </div>
      </div>

    )
  }
}
class Headlines extends React.Component{
  constructor(props){
    super(props)
    this.state={headlines:[],page:this.props.page}
    this.util=this.util.bind(this)
    addres=this
  }
  componentDidMount(){
this.util() 
 }
util(){
  this.setState((prev)=>{return {headlines:[],page:prev.page+1}});
  fetch(`https://newsapi.org/v2/everything?q=technology&page=${this.state.page}&pageSize=10&apiKey=9ed751fd08904a52a2ba4593563fe7d4`).then((res)=>res.json()).then((res)=>this.setState({headlines:res.articles,page:this.state.page}));

}
  render(){
    return(
      <div>
        {this.state.headlines.length>0 &&
        this.state.headlines.map((news)=><><div className='headlines'><img className='newsimg' src={news.urlToImage}></img><a href={news.url} className='newstext'>{news.title}<br></br><br></br><br></br>source::{news.source.name}</a></div><br></br></>)}
      </div>
    )
  }
}

ReactDOM.render(<News/>,document.getElementById('root'))
