import React from "react";
import ReactDOM from "react-dom";
import { responsive } from "./responsive.js";
import {Heading} from './heading.js'
let address=[0,0,0,0,0,0,0,0,0]
export class Questions extends React.Component{
    constructor(props){
        super(props)
        this.state={index:0,sidebar_style:responsive.sidebar_02.main(),image:responsive.questions.image(this.props.obj.image),heading:responsive.questions.heading()}
        this.handleclick=this.handleclick.bind(this)
        this.resize=this.resize.bind(this)
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState((prev)=>{return {index:prev.index,sidebar_style:responsive.sidebar_02.main(),image:responsive.questions.image(this.props.obj.image),
            heading:responsive.questions.heading()}})
    }
    handleclick(ind){
        this.setState({index:ind,sidebar_style:responsive.sidebar_02.main(),image:responsive.questions.image(this.props.obj.image),
            heading:responsive.questions.heading()})
    }
    componentDidUpdate(){
        for(let i=0;i<address.length;i++)address[i].resize()
    }
    render(){
        return(
            <div>
            <Heading/>
            <div style={this.state.image}>
            </div>
            <div style={this.state.heading}>
                {this.props.obj.h1}
            </div>

            <div style={this.state.sidebar_style}>
                {
            this.props.obj.subtopics.map((ele)=>{
            if(this.state.index!=ele[2])
           return <Subtopic func={this.handleclick} img={ele[1]} topic={ele[0]} index={ele[2]} flag={false}/>
            else
           return <Subtopic func={this.handleclick} img={ele[1]} topic={ele[0]} index={ele[2]} flag={true}/>
            })
                }
            </div>  
            <Q_bar link={'http://localhost:8000/cpp/get'}/>

         </div>
        )
    }

}
class Subtopic extends React.Component{
    constructor(props){
        super(props)
        address[this.props.index]=this
        this.state={flag:this.props.flag,style:responsive.sidebar_02.Element(this.props.flag),text:responsive.sidebar_02.text(),image:responsive.sidebar_02.image(this.props.img)}
        this.resize=this.resize.bind(this);
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState({style:responsive.sidebar_02.Element(this.props.flag),text:responsive.sidebar_02.text(),image:responsive.sidebar_02.image(this.props.img)});
    }
    render(){
        return(
            <div style={this.state.style} onClick={async ()=>{await this.props.func(this.props.index);}}>
            <div style={this.state.image}>
            </div>
              <text style={this.state.text}>
                            {this.props.topic}
              </text>
            </div>
        )
    }
}
class Q_bar extends React.Component{
    constructor(props){
        super(props)
        this.state={pre:1,slider:responsive.Q_bar.Main_slider(),style:responsive.Q_bar.next_button(),question_bar:responsive.Q_bar.question_bar(),arr:{}};
        fetch(this.props.link).then((res)=>res.json()).then((res)=>this.setState((prev)=>{return {question_bar:prev.question_bar,arr:res}}));
        this.resize=this.resize.bind(this)
        window.addEventListener('resize',this.resize)
        this.updatepre=this.updatepre.bind(this)
    }
    resize(){
        this.setState((prev)=>{return {pre:prev.pre,slider:responsive.Q_bar.Main_slider(),style:responsive.Q_bar.next_button(),question_bar:responsive.Q_bar.question_bar(),arr:prev.arr}})
    }
    updatepre(){
        if(this.state.pre==this.state.arr.length)return;
        this.setState((prev)=>{return {pre:prev.pre+1,slider:prev.slider,style:prev.style,question_bar:prev.question_bar,arr:prev.arr}})
    }
    render(){
        return (
        <div style={this.state.question_bar}>
            <div style={this.state.slider}>
                <button style={this.state.style} onClick={()=>{
                    if(this.state.pre==1)return;
                        this.setState((prev)=>{return {pre:prev.pre-1,slider:prev.slider,style:prev.style,question_bar:prev.question_bar,arr:prev.arr}})
                }}>
                    {'<'}
                </button>
                <Gap/>
                <Slider index={this.state.pre-1} flag-={false}/>
                <Slider index={this.state.pre} flag={true}/>
                <Slider index={this.state.pre+1} flag={false}/>
                <Gap/>
                <button style={this.state.style} onClick={this.updatepre}>
                    {'>'}
                </button>
            </div>
            {
                this.state.arr.length>0 && 
                    <text>
                    {this.state.arr[this.state.pre-1].question}
                    <br/>
                    <textarea >
                    {"hello \n world"}
                    </textarea>
                </text>
            }
        </div>

        )
    }

}
class Slider extends React.Component{
    constructor(props){
        super(props)
        this.state={style:responsive.Q_bar.slider(this.props.flag)}
        this.resize=this.resize.bind(this)
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState({style:responsive.Q_bar.slider(this.props.flag)})
    }
    render(){
        return(
            <button style={this.state.style}>{this.props.index}</button>
        )
    }
}
class Gap extends React.Component{
    constructor(props){
        super(props)
        this.state={style:responsive.Q_bar.gap()}
        this.resize=this.resize.bind(this)
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState({style:responsive.Q_bar.gap()});
    }
    render(){
        return(
            <div style={this.state.style}>
                ....
            </div>
        )
    }
}
