let width=window.innerWidth/100,height=window.innerHeight/100
export const responsive={
    heading:function(){
        let main={
            color:'wheat',
            height:`${5*height}px`,
            width:`${100*width}px`,
            fontSize:`${Math.min(3*height,5*width)}px`,
           // backgroundColor:'grey',
            position:'relative'
        }
        let hr={
            color:'wheat',
            height:`${1*height}px`,
            width:`${100*width}px`,
            position:'relative'
        }
        return [main,hr];
    },
    cpp:function(){
        let style={
            position:'relative',
            height:`${15*height}px`,
            width:`${20*width}px`,
            left:`${10*width}px`,
            top:`${10*height}px`,
            border:`${Math.min(0.5*height,0.5*width)}px`,
            boxShadow:`0px 2px 10px`,
            color:'grey',
            fontSize:`${Math.min(10*height,15*width)}px`,
            textAlign:'center',
            backgroundColor:'black'
        }
        return style
    },
    question:function(){
        let parent={
            display:'block',
            position:'relative',
            height:`${60*height}px`,
            top:`${20*height}px`,
            left:`${20*width}px`,
            width:`${60*width}px`,
            border:`${Math.min(height,width)}px`,
            overflow:'auto',
            backgroundColor:'white'
        }
        let q={
            position:'relative',
            height:`30%`,
            width:'100%',
            textAlign:'center'
        }
        let op1={
            position:'relative',
            height:`${10*height}px`,
            width:'100%',
            display:'block',float:'left'

        }
        let op2={
            position:'relative',
            height:`${10*height}px`,
            width:'100%',
            display:'block',float:'left'


        }
        let op3={
            position:'relative',
            height:`${10*height}px`,
            width:'100%',
            display:'block',float:'left'


        }
        let op4={
            position:'relative',
            height:`${10*height}px`,
            display:'block',float:'left'


        }
        return [parent,q,op1,op2,op3,op4]
    }
}
