import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'
import ArrowKeysReact from 'arrow-keys-react';
let mat=[]
let count=0
let r;
let align=2,row=0,col=0,sem=1,len=7,started=0,score=0;
let bool=[]

for(let i=0;i<30;i++)
{
    let temp=[];
    let t=[];
    for(let j=0;j<50;j++)
    {temp.push(0);t.push(0);}
    mat.push(temp);
    bool.push(t);
}

class Board extends React.Component{
  constructor (){
    super();

    ArrowKeysReact.config({
      left:()=>{
        while(sem==1);
        sem=1;
        if(align==2||align==4){sem=0;return;}
        align=4;
        sem=0;
      },
      right:()=>{
        while(sem==1);
        sem=1;
        if(align==4||align==2){sem=0;return;}
        align=2;
        sem=0;
      },
      up:()=>{
        while(sem==1);
        sem=1;
        if(align==1||align==3){sem=0;return;}
        align=1;
        sem=0;
      },
      down:()=>{
        while(sem==1);
        sem=1;
        if(align==1||align==3){sem=0;return;}
        align=3;
        sem=0;
      }

    });
  }
componentDidMount(){
    r=document.getElementById('root');
  };

    render()
    {
        return(
          <div style={{height:'880px'}}{...ArrowKeysReact.events} tabIndex='1' >
            <div className='Board'>
               {mat.map((mat)=><Row1 mat={mat}/>)}
            </div>
            <div className='Menu'>
            <Start /><Pause /><Resume /><Score />
            </div>
            </div>
        )
    }
}
class Row1 extends React.Component{
    constructor(props)
    {
        super(props)
    }
    render(){
        return(
            <div className='Row' >
            {this.props.mat.map(()=><Element />)}
            </div>
        )
    }
}
class Element extends React.Component{
    constructor(){
        super()   
    }

    render(){
        return (
            <div className='Element' >
            </div>
        )
    }
}

let queue=[],book=[]
book.push(0);
book.push(0);

export function generate(){
  sem=1;
  let row=Math.floor(Math.random()*29)
  let col=Math.floor(Math.random()*49)
   book[0]=row;
   book[1]=col;

   if(bool[row][col]===1)generate();
   r.childNodes[0].childNodes[0].childNodes[row].childNodes[col].style.backgroundColor="black";

   sem=0;

}
let speed=100;
export function util()
{

  setTimeout(()=>{
    if(sem==1){util();return;}

     if(row==book[0] && col==book[1]){
      len+=1;
      score+=5;
      window.helloComponent.a();
      generate();
      speed-=2;
     }

    r.childNodes[0].childNodes[0].childNodes[row].childNodes[col].style.backgroundColor="black"
    if(bool[row][col]===1){
      alert("GAME OVER")
    sem=1;
    while(queue.length>0)
    {
      let t=queue.shift()
      r.childNodes[0].childNodes[0].childNodes[t[0]].childNodes[t[1]].style.backgroundColor="rgb(32, 99, 32)";
      bool[t[0]][t[1]]=0;
    }
    r.childNodes[0].childNodes[0].childNodes[book[0]].childNodes[book[1]].style.backgroundColor="rgb(32, 99, 32)";
    score=0;
    window.helloComponent.a();

    started=0;
    return;
    }

    if(queue.length==len)
    {
      let t=queue.shift()
      r.childNodes[0].childNodes[0].childNodes[t[0]].childNodes[t[1]].style.backgroundColor="rgb(32, 99, 32)";
      bool[t[0]][t[1]]=0;

    }
    queue.push([row,col]);
    bool[row][col]=1;
    if(align==1)
    {
      if(row==0)row=29;
      else
      row--;
      util();
    }
    else if(align==2)
    {
      col=(col+1)%50;
      util();
    }
    else if(align==3)
    {
      row=(row+1)%30;
      util()
    }
    else{
      if(col==0)col=49;
      else
      col--;
      util();
    }
  },speed)
}
class Resume extends React.Component{
 constructor(props){
  super(props)
  this.handleContinue=this.handleContinue.bind(this);
 }
  handleContinue(){
    sem=0;
  }
  render()
  {
    return (
      <div>
      <button className="Resume"onClick={this.handleContinue}>RESUME</button>
      </div>
    )
  }
}
class Pause extends React.Component{
  constructor(props){
    super(props)
    this.handlePause=this.handlePause.bind(this);
  }
  handlePause(){
    sem=1;

  }
  render(){
    return(
      <div>
        <button className='Pause' onClick={this.handlePause}>PAUSE</button>
      </div>
    )
  }
}
class Start extends React.Component{
  constructor(){
    super()
    this.handle=this.handle.bind(this);
  }
  handle(){
    sem=0;
    if(started==0){started=1;align=2;row=0;col=0;sem=1;len=7;score=0;speed=100;generate();
      util();}
  }
  render(){
    return(
      <div >
        <button className='Start' onClick={this.handle}>START</button>
      </div>
    )
  }
}
class Score extends React.Component{
  constructor(props){
    super(props)
    this.state={scr:score}
    window.helloComponent = this;

  }
  a(){
    this.setState({scr:score})
  }
  render(){
    return(
      <div className='Score'>
        SCORE:{this.state.scr}
      </div>
    )
  }
}
ReactDOM.render(<Board/>,document.getElementById('root'))
