import React from 'react'
import  ReactDOM  from 'react-dom'
let q={
    question:'',
    op1:'',
    op2:'',
    op3:'',
    op4:'',
    ans:'',
    explain:''
}
function style(){
    let width=window.innerWidth/100,height=window.innerHeight/100
    let q={
        height:`${30*(height)}px`,
        width:`${50*(width)}px`,
        position:'relative',
        border:`5px solid black`,
        top:`${10*height}px`,
        left:`${20*width}px`,
        textAlign:'left'
    }
    return [q]
}
class Add extends React.Component{
    constructor(){
        super()
        let arr=style()
        this.state={question:arr[0]}
        this.add=this.add.bind(this)
    }
    add(){
        const data={
            question:q.question,
            option_01:q.op1,
            option_02:q.op2,
            option_03:q.op3,
            option_04:q.op4,
            answer:q.ans,
            explaination:q.explain
        }
        fetch('http://localhost:8000/cpp/post',{
            method:'POST',
            headers:{
            'Content-type':'application/json',
            },
            body:JSON.stringify(data)
        })
    }
    render(){
        return(
            <div>
                <input type='text'placeholder='enter here' style={this.state.question} onKeyUp={(e)=>q.question=e.target.value}>
                </input>
                <button onClick={this.add}>ADD</button>
            </div>
        )
    }
}
ReactDOM.render(<Add/>,document.getElementById('root'));
