let width=window.innerWidth/100,height=window.innerHeight/100
export const responsive={
    heading:function(){
        let main={
            color:'wheat',
            height:`${5*height}px`,
            width:`${100*width}px`,
            fontSize:`${Math.min(3*height,5*width)}px`,
           // backgroundColor:'grey',
            position:'relative'
        }
        let hr={
            color:'wheat',
            height:`${1*height}px`,
            width:`${100*width}px`,
            position:'relative'
        }
        return [main,hr];
    },
    question:function(){
        let parent={
            display:'inline-block',
            position:'relative',
            height:`${60*height}px`,
            top:`${20*height}px`,
            left:`${20*width}px`,
            width:`${60*width}px`,
            border:`${Math.min(height,width)}px`,
            overflow:'auto',
            backgroundColor:'white',
            fontSize:'larger'
        }
        let q={
            position:'relative',
            height:`30%`,
            width:'100%',
            textAlign:'center',
            fontSize:'larger'
        }
        let op1={
            position:'relative',
            height:`${8*height}px`,
           // width:'100%',
            clear:'both',
            display:'block'

        }
      let button={
        backgroundColor:'light green',
        height:`${4*height}px`,
        left:`${100*width}px`,
        top:`${80*height}px`,
        fontSize:`${Math.min(3*height,5*width)}px`,
       // position:'relative',
        width:`${5*width}px`
      }
        return [parent,q,op1,button]
    },
    dsa:{
        Main:function(){
        let style={
            position:'relative',
            height:`${25*height}px`,
            width:`${80*width}px`,
            left:`${15*width}px`,
            bottom:`${70*height}px`,
            border:`${Math.min(0.5*height,0.5*width)}px`,
            boxShadow:`0px 2px 10px`,
            color:'grey',
            fontSize:`${Math.min(5*height,5*width)}px`,
            textAlign:'left',
            background:'transparent'
        }
        return style
       },
       text:function(){
        let style={
            background:'transparent',
            fontSize:`${Math.min(3*height,3*width)}px`,
            color:'grey',
            height:`${25*height}px`,
            border:'0px solid black'
        }
        return style
       }
       },
       sidebar:{
        main:function(){
            let style={
                position:'relative',
                height:`${80*height}px`,
                width:`${10*width}px`,
                border:`${Math.min(0.5*height,0.5*width)}px solid grey`,
                color:'white',

            }
            return style
        },
        Element:function(){
            let style={
                position:'relative',
                height:`${5*height}px`,
                width:`${7*width}px`,
                border:`${Math.min(0.1*height,0.1*width)}px solid grey`,
            }
            return style;
        }
       }
}
