import React from "react";
import ReactDOM from "react-dom";
import { Heading } from "../heading.js";
//import { Heading } from "../heading.js";
import { responsive } from "./responsive.js";
function checkvalidity(username)
{
    let flag=false;
    let code=document.getElementById('passcode').value
    let req={
        username:username,
        passcode:code
    }
    fetch('http://localhost:8000/hosting/isValid',{
        method:'POST',
        body:JSON.stringify(req),
        headers:{
            'Content-type':'application/json'
        }
    }).then((res)=>res.json()).then((res)=>{
        alert(res.valid)
       // flag=res.valid
    })
}
export class Hosting extends React.Component{
    constructor(props){
        super(props)
        this.state={style:responsive.hosting_header()}
    }
    render(){
        return (<div>
            <Heading/>
            <label htmlFor="passcode">Passcode</label>
            <input type="text" id="passcode"/>
            <button onClick={()=>{
                checkvalidity(this.props.username)
            }}>Submit</button>
        </div>
        )
    }
}
