import React, { useState } from 'react'
import  ReactDOM  from 'react-dom'
import './index.css'
import image from './robot.png'
import leftarrow from './leftarrow.png'
import rightarrow from './right-arrow.png'
import downarrow from './down-arrow.png'
import uparrow from './up-arrow.png'

let root=ReactDOM.createRoot(document.getElementById('root'));
let mat=[],logic=[];
let current;
for(let i=0;i<5;i++){
  let t=[];
  for(let j=0;j<5;j++)
  t.push(0);
  mat.push(t);
}
let t=document.createElement("img")
t.src=image
t.className="image"
for(let i=0;i<15;i++)logic.push(-1);
let r;
class Page extends React.Component{
  componentDidMount(){
    r=document.getElementById('root');
    r.childNodes[1].childNodes[0].childNodes[0].appendChild(t)
    r.childNodes[1].childNodes[4].childNodes[4].style.backgroundColor="red"
  }

  render(){
    return(
      <><Learn/><Grid/><Instruction/><Logicpanel/><Controls/><Play text="play"/><Play text="reset"/></>
    )
  }
}
function Grid(){
  return(
    <div className='Grid'>
    {mat.map((arr,index)=><Row arr={arr} />)}
    </div>
  )
}
function Row(props){
return(
  <div className='Row'>
    {props.arr.map((element)=><Element val={element} />)}
  </div>
)
}
class Element extends React.Component{
  constructor(props)
  {
    super(props)
    this.state={flag:this.props.val}
  }

  render(){
    return(
      <div className='Element'> 
        {this.state.flag==1 &&
        <img src={image} className="image"></img>}
      </div>
    )
  }
}
function Instruction(){
  return (
    <div className='instruction'>
      <div style={{backgroundColor:'blue',height:'80px',textAlign:'center',fontSize:'30px'}}>
      <li>Instruction Implemented</li>
      </div>
    </div>
  )
}
function Learn(){
return(
  <div className='learn'>

  </div>
)
}
function Panelbutton(props){
  const [val,setVal]=useState('');
  const index=props.index;

  return(
    <div draggable onDragOver={(e)=>{e.preventDefault();setVal(current);logic[index]=current}} className='panelbutton' value={val}>
      {val=='U' && <img src={uparrow} className='arrow1'></img>}
      {val=='D' && <img src={downarrow} className='arrow1'></img>}
      {val=='L' && <img src={leftarrow} className='arrow1'></img>}
      {val=='R' && <img src={rightarrow} className='arrow1'></img>}
      </div>
  )
}
function Logicpanel(){
  const items=[];
  for(let i=0;i<15;i++)
  items.push(<Panelbutton index={i}/>)
  return(
    <div className='logicpanel' >
      <li>Logicpanel</li>
      {items.map((ele)=>{return ele})}

    </div>
  )
}
function Controls(){
  return(
    <div className='controls'>
      <Controlbutton val='U'/>
      <Controlbutton val='R'/>
      <Controlbutton val='D'/>
     < Controlbutton val='L'/>
    </div>
  )
}
class Controlbutton extends React.Component{
  constructor(props){
    super(props)
    this.state={val:props.val}
    this.handle=this.handle.bind(this);
  }
  handle(){
    current=this.state.val;
  }
  render(){
    return(
      <div className='controlbutton' draggable onDrag={this.handle}>
        {this.state.val=='U'&& <img src={uparrow} className='arrow'></img>}
        {this.state.val=='D' && <img src={downarrow}className='arrow'></img>}
        {this.state.val=='R'&& <img src={rightarrow} className='arrow'></img>}
        {this.state.val=='L'&& <img src={leftarrow} className='arrow'></img>}

      </div>
    )
  }
}
let row=0,col=0,i=0;

function Play(props){
function handle(){
  if(i==logic.length || logic[i]==-1)return;

  setTimeout(()=>{
    if(i==logic.length || logic[i]==-1)return;
    if(logic[i]=='U'){
      if(row==0)return;
      r.childNodes[1].childNodes[row].childNodes[col].removeChild( r.childNodes[1].childNodes[row].childNodes[col].firstChild)
      r.childNodes[1].childNodes[row-1].childNodes[col].appendChild(t);
      row--;

    }
    else if(logic[i]=='D'){
      if(row==4)return;
      r.childNodes[1].childNodes[row].childNodes[col].removeChild( r.childNodes[1].childNodes[row].childNodes[col].firstChild)
      r.childNodes[1].childNodes[row+1].childNodes[col].appendChild(t);
      row++;
    }
    else if(logic[i]=='L'){
      if(col==0)return;
      r.childNodes[1].childNodes[row].childNodes[col].removeChild( r.childNodes[1].childNodes[row].childNodes[col].firstChild)
      r.childNodes[1].childNodes[row].childNodes[col-1].appendChild(t);
      col--;
    }
    else{
      if(col==4)return;
      r.childNodes[1].childNodes[row].childNodes[col].removeChild( r.childNodes[1].childNodes[row].childNodes[col].firstChild)
      r.childNodes[1].childNodes[row].childNodes[col+1].appendChild(t);
      col++;
    }
    i++;
    handle();
  },1000);
}
  return(
    <button className='play' onClick={handle}>
      {props.text}
    </button>
  )
}
root.render(<Page/>);

setTimeout(()=>{root.render(<Page/>)},3000);
