import React from 'react'
import ReactDOM from 'react-dom'
import { responsive_mock } from './responsive.js'
export class Test_page extends React.Component{
    constructor(props){
        super(props)
        this.state={index:0,time:10,timer:responsive_mock.test_page.timer(),question:responsive_mock.test_page.question(),tab:responsive_mock.test_page.tab()
        ,question_bar:responsive_mock.test_page.question_bar()}
        this.resize=this.resize.bind(this)
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState((prev)=>{return {index:prev.index,time:prev.time,timer:responsive_mock.test_page.timer(),question:responsive_mock.test_page.question(),tab:responsive_mock.test_page.tab()
            ,question_bar:responsive_mock.test_page.question_bar()}})
    }
    componentDidMount(){
        this.starttimer()
    }
    starttimer(){
        if(this.state.time==0){
            if(this.state.index==9)return;
            this.setState((prev)=>{return {index:prev.index+1,time:10,timer:prev.timer}},this.starttimer);
            return;
        }
        setTimeout(()=>
        {
        this.setState((prev)=>{return {index:prev.index,time:prev.time-1,timer:prev.timer}},this.starttimer)},1000);
    }
    render(){
        return<div>
            <div style={this.state.question_bar}>
            {this.props.arr.map((ele,ind)=><div style={this.state.tab}>{ind}</div>)}
            </div>
            <div style={this.state.timer}>{'00:'+this.state.time}</div>
            <div style={this.state.question}>{this.props.arr[this.state.index][0]}</div>

        </div>
    }
}
