import React from "react";
import ReactDOM  from "react-dom";
import { responsive } from "./responsive.js";
import cppimg from './cpp.png'
import javaimg from './java.png'
import reactimg from './react.png'
import dsimg from './ds.png'
import algoimg from './algorithms.png'
import jsimg from './js.png'
import osimg from './Os.png'
import dbmsimg from './Dbms.png'
let arr=[['C++',cppimg,0],['Java',javaimg,1],['React',reactimg,2],['Data\nStructures',dsimg,3],['Algorithms',algoimg,4],['JavaScript',jsimg,5],['Operating\nSystems',osimg,6]
,['DBMS',dbmsimg,7]]

export class Scroll_header extends React.Component{
    constructor(){
        super()
        this.state={index:1,style:responsive.scroll_bar.Main()}
        this.resize=this.resize.bind(this)
        window.addEventListener('resize',this.resize)
        this.handleleftclick=this.handleleftclick.bind(this)
        this.handlerightclick=this.handlerightclick.bind(this)
    }
    componentDidUpdate(){
        alert(1)
    }
    resize(){
        this.setState((prev)=>{return {index:prev.index,style:responsive.scroll_bar.Main()}})
    }
    handleleftclick(){
      //  alert(arr.length)
       // alert(Math.abs((this.state.index-2)%(arr.length-1)))
        this.setState((prev)=>{return {index:Math.abs((prev.index-1)%(arr.length-1)),style:prev.style}})
        return;
    }
    handlerightclick(){
        this.setState((prev)=>{return {index:(prev.index+1)%(arr.length-1),style:prev.style}});
        return;

    }
    render(){
        return (
            <div style={this.state.style}>
                <button onClick={this.handleleftclick}>{'<'}</button>
                <Scroll flag={0} img={arr[Math.abs((this.state.index-1)%(arr.length-1))][1]} h1={arr[Math.abs((this.state.index-1)%(arr.length-1))][0]} ind={arr[Math.abs((this.state.index-1)%(arr.length-1))][2]}/>
                <Scroll flag={1}img={arr[this.state.index][1]} h1={arr[this.state.index][0]} ind={arr[this.state.index][2]}/>
                <Scroll flag={0} img={arr[((this.state.index+1)%(arr.length-1))][1]} h1={arr[(this.state.index+1)%arr.length][0]} ind={arr[(this.state.index+1)%arr.length][2]}/>
                <button onClick={this.handlerightclick}>{'>'}</button>
            </div>
        )
    }
}

class Scroll extends React.Component{
    constructor(props){
        super(props)
        this.state={style:responsive.scroll_bar.Element(this.props.flag),image:responsive.scroll_bar.image(this.props.flag,this.props.img),text:responsive.scroll_bar.text(this.props.flag)};
    }
    componentDidUpdate(){
        alert(2)
    }
    resize(){
        this.setState({style:responsive.scroll_bar.Element(this.props.flag),image:responsive.scroll_bar.image(this.props.flag,this.props.img),text:responsive.scroll_bar.text(this.props.flag)})
    }
    render(){
        return(
            <div style={this.state.style}>
                <div style={this.state.image}>
                </div>

            </div>
        )
    }
}
