import React from "react";
import ReactDOM from "react-dom";
import { responsive } from "./responsive.js";
import {Heading} from './heading.js'

export class Questions extends React.Component{
    constructor(props){
        super(props)
        this.state={index:0}
        this.handleclick=this.handleclick.bind(this)
    }
    handleclick(ind){
        this.setState({index:ind})
    }
    render(){
        return(
            <div>
            <Heading/>
            <Side_bar arr={this.props.obj.subtopics} func={this.handleclick}/>
            </div>
        )
    }

}
class Side_bar extends React.Component{
    constructor(props)
    {
        super(props)
        this.state={style:responsive.sidebar_02.main()}
    }
    render(){
        return(
            <div style={this.state.style}>
                {
            this.props.arr.map((ele)=><Subtopic func={this.props.func} img={ele[1]} topic={ele[0]} index={ele[2]}/>)
                }
            </div>
        )
    }
}
class Subtopic extends React.Component{
    constructor(props){
        super(props)
        this.state={style:responsive.sidebar_02.Element(),text:responsive.sidebar_02.text(),image:responsive.sidebar_02.image(this.props.img)}
        this.resize=this.resize.bind(this);
        window.addEventListener('resize',this.resize)
    }
    resize(){
        this.setState({style:responsive.sidebar.Element(),text:responsive.sidebar.text(),image:responsive.sidebar.image(this.props.img)});
    }
    render(){
        return(
            <div style={this.state.style} onClick={this.props.func(this.props.index)}>
            <div style={this.state.image}>

            </div>
              <text style={this.state.text}>
                            {this.props.topic}
              </text>
        </div>
        )
    }
}
