import './index.css'
import './snake.css'
import ReactDOM from 'react-dom'
let mat=[]
let count=0
for(let i=0;i<20;i++)
{
    let temp=[]
    for(let j=0;j<50;j++)
    temp.push(0);
    mat.push(temp)
}

export  class Board extends React.Component{
  componentDidMount(){
    generate();
    util();
  }
    render()
    {
        return(
          <>
            <div className='Board'>
               {mat.map((mat)=><Row mat={mat}/>)}
            </div>
            <div>
            <Button/>
            </div>
            <div className='Menu'>
              <Resume/>
            </div>
            </>
        )
    }
}
class Row extends React.Component{
    constructor(props)
    {
        super(props)
    }
    render(){
        return(
            <div className='Row' >
            {this.props.mat.map((ele)=><Element />)}
            </div>
        )
    }
}
