}
let align=2,row=0,col=0,sem=0,len=7;
class Button extends React.Component{
  handle(e){
    sem=1;
    let v=e.target.value;
    v=v[0]
    e.target.value="";
    if(v=='w')
    {if(align==1||align==3){sem=0;return;}
    align=1;
  }
    else if(v=='a')
    {
      if(align==2||align==4){sem=0;return;}
      align=4;
    }
    else if(v=='d')
      {if(align==4||align==2){sem=0;return;}
      align=2;
    }
    else
    {if(align==1||align==3){sem=0;return;}
    align=3;
  }
    sem=0;
    return;
  }
  render(){
    return(
      <div >
         <input type="text" onKeyUp={this.handle}/>
      </div>
    )
  }
}
let queue=[],book=[]
book.push(0);
book.push(0);
let r=document.getElementById('root');

export function generate(){

  let row=Math.floor(Math.random()*19)
  let col=Math.floor(Math.random()*49)
  r.childNodes[0].childNodes[row].childNodes[col].style.backgroundColor="black";
   book[0]=row;
   book[1]=col;
   for(let i=0;i<queue.length;i++)
   if(queue[i][0]==row&&queue[i][1]==col)generate();

}
let speed=100;
export function util()
{

  setTimeout(()=>{
    if(sem==1){util();return;}

     if(row==book[0] && col==book[1]){
      len+=1;
      generate();
      speed-=8;
     }
    r.childNodes[0].childNodes[row].childNodes[col].style.backgroundColor="black"


    if(queue.length==len)
    {
      let t=queue.shift()
      r.childNodes[0].childNodes[t[0]].childNodes[t[1]].style.backgroundColor="white";


    }
    queue.push([row,col]);
    if(align==1)
    {
      if(row==0)row=19;
      else
      row--;
      util();
    }
    else if(align==2)
    {
      col=(col+1)%50;
      util();
    }
    else if(align==3)
    {
      row=(row+1)%20;
      util()
    }
    else{
      if(col==0)col=49;
      else
      col--;
      util();
    }
  },speed)
}
class Resume extends React.Component{
  handlePause(){
    sem=1;
  }
  handleContinue(){
    sem=0;
  }
  render()
  {
    return (
      <div>
      <button className='Pause' onClick={this.handlePause}>PAUSE</button>
      <button className="Continue"onClick={this.handleContinue}>CONTINUE</button>
      </div>
    )
  }
}
