import React from "react";
import ReactDOM from "react-dom";
import { responsive_auth} from "./responsive.js";

export class Authentication extends React.Component{
    constructor(){
        super()
        this.state={details:{username:'',password:''},create:false,style:responsive_auth.Main.cover(),button:responsive_auth.Main.button(),heading:responsive_auth.Main.heading(),signin:responsive_auth.Main.signin(),
            header:responsive_auth.Main.header(),username:responsive_auth.Main.username(),label:responsive_auth.Main.usernamelabel()}
            this.handleclick=this.handleclick.bind(this)
            this.createaccount=this.createaccount.bind(this)
    }
    handleclick(){
        this.setState((prev)=>{return {new_user:{email:'',username:'',password:''},style:prev.style,create:true}})
    }
    createaccount(){
        fetch('http://localhost:8000/auth/new_user',{
            method:'POST',
            body:JSON.stringify(this.state.new_user),
             headers:{
                'Content-type':'application/json'
             }
        });
        this.props.login(this.state.details)
    }
    render(){

        return <div style={this.state.style}>
            {
                !this.state.create&&
            <form style={this.state.header}>
                <h1 style={this.state.heading}>Login To Continue</h1>
                <label  style={this.state.label}htmlFor="username">Username</label>
                <br/>
                <input style={this.state.username}id='username' autoFocus={true}/>
                <br/><br/>
                <label style={this.state.label}htmlFor="password">Password</label>
                <br/>
                <input style={this.state.username} id="Password"/>
                <button style={this.state.signin}>Sign in</button>
                <button style={this.state.button} onClick={this.handleclick}>
                    Create Account
                </button>
            </form>
           }
           {
            this.state.create&&
            <form style={this.state.header}>
            <h1 style={this.state.heading}>Login To Continue</h1>
            <label  style={this.state.label}htmlFor="Email">Email</label>
            <br/>
            <input style={this.state.username}id='Email' autoFocus={true}/>
            <br/><br/>
            <label style={this.state.label}htmlFor="password">New Password</label>
            <br/>
            <input style={this.state.username} id="password"/>
            <br/><br/>
            <label style={this.state.label}htmlFor="reenter">Confirm password</label>
            <br/>
            <input style={this.state.username} id="reenter"/>
            <button style={this.state.button} onClick={this.createaccount}>
                Create Account
            </button>
        </form>
           }
        </div>
    }
}
