import ReactDOM from 'react-dom'
import React from 'react'
import './index.css'
class News extends React.Component{
  constructor(){
    super()
    this.state={page:1}
  }
  render(){
    return(
      <div>
      <div className='Heading'>
      <div className='newslogo'></div>
      <div className='newsheading'>NEWS24</div>
      </div>
      <hr/>
      <Headlines page={this.state.page}/>
      <Pagenation/>
      </div>

    )
  }
}
class Headlines extends React.Component{
  constructor(props){
    super(props)
    this.state={headlines:[]}
  }
  componentDidMount(){
    fetch(`https://newsapi.org/v2/top-headlines?country=in&page=${this.props.page}&apiKey=9ed751fd08904a52a2ba4593563fe7d4`).then((res)=>res.json()).then((res)=>this.setState({headlines:res.articles}));
  }
  render(){
    return(
      <div>
        {this.state.headlines.length>0 &&
        this.state.headlines.map((news)=><><div className='headlines'><img className='newsimg' src={news.urlToImage}></img><a href={news.url} className='newstext'>{news.title}<br></br><br></br><br></br>{news.source.name}</a></div><br></br></>)}
      </div>
    )
  }
}
class Pagenation extends React.Component{

}
ReactDOM.render(<News/>,document.getElementById('root'))
