import React from "react";
import { responsive } from "./responsive.js";
export default class Questions extends React.Component{
    constructor(props){
        super(props)
        let arr=responsive.question();
        this.state={index:0,parent:arr[0],q:arr[1],op1:arr[2],button:arr[3]}
    }
    render(){
        return(
            <div style={this.state.parent}>
                <textarea style={this.state.q} value={this.props.q[this.state.index].question}></textarea>
                < div style={this.state.op1} >
                <input type='checkbox' id="input_01"></input>
                <label htmlFor="input_01">{this.props.q[this.state.index].option_01}</label>
                </div>
                <div style={this.state.op1}>
                <input  type='checkbox' id="input_02"></input>
                <label htmlFor="input_02">{this.props.q[this.state.index].option_02}</label>
                </div>
                <div style={this.state.op1}>
                <input  type='checkbox' id="input_03"></input>
                <label htmlFor="input_03">{this.props.q[this.state.index].option_03}</label>
                </div>
                <div style={this.state.op1}>
                <input  type='checkbox' id="input_04"></input>
                <label  htmlFor="input_04">{this.props.q[this.state.index].option_04}</label>
                <button style={this.state.button}onClick={()=>{this.setState((prev)=>{return {index:prev.index+1,q:prev.q,op1:prev.op1,button:prev.button}})}}>NEXT.</button>
                </div>
            </div>
        )
    }
}
