import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'
import './snake.css'
let mat=[]
let count=0
let refs=[]
for(let i=0;i<40;i++)
{
  let t=[]
    let temp=[]
    for(let j=0;j<75;j++)
    {t.push(null);temp.push(0);}
    mat.push(temp)
    refs.push(t);
}

refs=JSON.parse(JSON.stringify(mat))
class Board extends React.Component{
    render()
    {
        return(
            <div className='Board'>
               {mat.map((mat)=><Row mat={mat}/>)}
               <Button />
            </div>
        )
    }
}
class Row extends React.Component{
    constructor(props)
    {
        super(props)
    }
    render(){
        return(
            <div className='Row' >
            {this.props.mat.map((ele)=><Element />)}
            </div>
        )
    }
}
let tep;
class Element extends React.Component{
    constructor(){
        super()


    }

    render(){
        return (
            <div className='Element' >

            </div>
        )
    }
}
let align=2,row=0,col=0,sem=0,len=7;
class Button extends React.Component{
  up(){
    if(align==1||align==3)return;

    align=1;

    return;
  }
  down(){
    if(align==1||align==3)return;

    align=3;

    return;
  }
  left(){
    if(align==2||align==4)return;
    align=4;
    return;
  }
  right(){
    if(align==2||align==4)return;
    //col=(col+1)%75;
    //row--;
    align=2;

    return;
  }
  handle(e){
    sem=1;
    let v=e.target.value;
    e.target.value="";
    if(e=='w')
    this.up()
    else if(e=='a')
    this.right()
    else if(e=='d')
    this.left();
    else
    this.down();
    sem=0;
    return;
  }
  render(){
    return(
      <div style={{position:'relative',left:'800px',bottom:'500px'}}>
         <input type="text" onKeyUp={this.handle}/>
      </div>
    )
  }
}
let r=document.getElementById('root')
ReactDOM.render(<Board/>,r)
let queue=[],book=[]
book.push(0);
book.push(0);
function generate(){
  let row=Math.floor(Math.random()*39)
  let col=Math.floor(Math.random()*74)
  r.childNodes[0].childNodes[row].childNodes[col].style.backgroundColor="black";
   book[0]=row;
   book[1]=col;

}
let speed=100;
function util()
{
  if(sem==1){util();return;}

  setTimeout(()=>{
    if(sem==1){util();return;}
     if(row==book[0] && col==book[1]){
      len+=5;
      generate();
      speed-=30;
     }
    r.childNodes[0].childNodes[row].childNodes[col].style.backgroundColor="black";
    if(queue.length==len)
    {
      let t=queue.shift()
      r.childNodes[0].childNodes[t[0]].childNodes[t[1]].style.backgroundColor="white";
    }
    queue.push([row,col]);
    if(align==1)
    {
      if(row==0)row=39;
      else
      row--;
      util();
    }
    else if(align==2)
    {
      col=(col+1)%75;
      util();
    }
    else if(align==3)
    {
      row=(row+1)%40;
      util()
    }
    else{
      if(col==0)col=74;
      else
      col--;
      util();
    }
  },speed)
}
