import React from 'react'
import ReactDOM from 'react-dom'
//import responsive from './responsive.js'
import {Heading} from './heading.js'
import Cpp from './cpp.js'
import Questions from './questions.js'
import Add from './input.js'
import Dsa from './dsa.js'
document.body.style.backgroundColor='black'
class Main extends React.Component{
    constructor(props){
        super(props)
        this.func=this.func.bind(this)
        this.dsa_func=this.dsa_func.bind(this)
         this.state={cpp_questions:[],main:true}
    }

    async func(){
        let questions=[]
       await fetch("http://localhost:8000/cpp/get").then((res)=>res=res.json()).then((res)=>questions=res)
       this.setState({cpp_questions:questions,main:false})

    }
    async dsa_func(){
        let questions=[]
        await fetch("http://localhost:8000/dsa/get").then((res)=>res.json()).then((res)=>questions=res)
        this.setState({cpp_questions:questions,main:false})
    }
    render(){
        return(
            <>
         <Heading/>
         {this.state.cpp_questions.length>0 && <>
            <Questions q={this.state.cpp_questions}/>
         </>}
         {this.state.main &&
         <>
        <Cpp func={this.func}/>
        <Dsa func={this.dsa_func}/>
        </>
        }
        </>       
         )
    }
}
ReactDOM.render(<Main/>,document.getElementById('root'));
