import ReactDOM from 'react-dom'
import React from 'react'
import './index.css'
let addres;

class News extends React.Component{
  constructor(){
    super()
    this.state={page:1}
  }

  render(){
    return(
      <div>
      <div className='Heading'>
      <div className='newslogo'></div>
      <div className='newsheading'>NEWS24</div>
      </div>
      <hr/>
      <Filters/>
      <Headlines/>
      <div className='Pagenation'>
        {this.state.page>1 && <button style={{height:'30px',width:'30px',backgroundColor:'black',color:'white'}} onClick={()=>{this.setState((prev)=>{return {page:prev.page-1}});addres.util(0)}}> {"<"} </button>}
        <button style={{height:'30px',width:'30px',backgroundColor:'black',color:'white'}} onClick={()=>{this.setState((prev)=>{return {page:prev.page+1}});addres.util(1)}}>{">"}</button>
      </div>
      </div>

    )
  }
}
class Headlines extends React.Component{
  constructor(props){
    super(props)
    this.state={headlines:[],page:2,lang:'en'}
    this.util=this.util.bind(this)
    this.changelang=this.changelang.bind(this)
    addres=this
  }
  componentDidMount(){
this.util(1) 
 }
util(flag){
  if(flag==1)
  this.setState((prev)=>{return {headlines:[],page:prev.page+1,lang:prev.lang}});
  else
  this.setState((prev)=>{return {headlines:[],page:prev.page-1,lang:prev.lang}});

  fetch(`https://newsapi.org/v2/everything?q=technology&language=${this.state.lang}&page=${this.state.page}&pageSize=1&apiKey=9ed751fd08904a52a2ba4593563fe7d4`).then((res)=>res.json()).then((res)=>this.setState({headlines:res.articles,page:this.state.page,lang:this.state.lang}));

}
changelang(l){
  this.setState((prev)=>{return {headlines:[],page:0,lang:l}});
  fetch(`https://newsapi.org/v2/everything?q=technology&language=${l}&page=${this.state.page}&pageSize=1&apiKey=9ed751fd08904a52a2ba4593563fe7d4`).then((res)=>res.json()).then((res)=>this.setState({headlines:res.articles,page:this.state.page,lang:l}));


}

  render(){
    return(
      <div>
        {this.state.headlines!=undefined &&
        this.state.headlines.map((news)=><><div className='headlines'><img className='newsimg' src={news.urlToImage}></img><a href={news.url} className='newstext'>{news.title}<br></br><br></br><br></br>source::{news.source.name}</a></div><br></br></>)}
      </div>
    )
  }
}
class Filters extends React.Component{
  constructor(){
    super()
    this.state={lang:{
      flag:false,avail:['Arabic','English','French','Italian','German','Armenian','Hebrew'],default:'English'
    }}
    this.handleLanguage=this.handleLanguage.bind(this);
  }
  handleLanguage(){
    if(this.state.lang.flag==false)
this.setState((prev)=>{return {lang:{flag:true,avail:prev.lang.avail}}})
else
this.setState((prev)=>{return {lang:{flag:false,avail:prev.lang.avail}}})
  }
  render(){
    return(
      <div className='filter'>
        Filters:
        <div>
          Language:<button style={{heigth:'60px',width:'200px',color:'black'}}onClick={this.handleLanguage}>{this.state.lang.default}</button>
          {this.state.lang.flag==true && this.state.lang.avail.map((l)=><div onClick={()=>{let n=l.charCodeAt(0)+32;let temp=String.fromCharCode(n);temp+=l[1];addres.changelang(temp);this.setState((prev)=>{return {lang:{flag:false,avail:prev.lang.avail,default:l}}})}} style={{color:'white',height:'40px',width:'200px',border:'2px solid black',borderRadius:'10px',backgroundColor:'black'}}>{l}</div>)}
        </div>
      </div>
    )
  }
}

ReactDOM.render(<News/>,document.getElementById('root'))
