import ReactDOM from 'react-dom'
import React from 'react'
import './index.css'
let addres;

class News extends React.Component{
  constructor(){
    super()
    this.state={page:1}
  }

  render(){
    return(
      <div>
      <div className='Heading'>
      <div className='newslogo'></div>
      <div className='newsheading'>NEWS24</div>
      </div>
      <hr/>
      <Headlines/>
      <div className='Pagenation'>
        {this.state.page>1 && <button style={{height:'30px',width:'30px',backgroundColor:'black',color:'white'}} onClick={()=>{this.setState((prev)=>{return {page:prev.page-1}});addres.util(0)}}> {"<"} </button>}
        <button style={{height:'30px',width:'30px',backgroundColor:'black',color:'white'}} onClick={()=>{this.setState((prev)=>{return {page:prev.page+1}});addres.util(1)}}>{">"}</button>
      </div>
      </div>

    )
  }
}
class Headlines extends React.Component{
  constructor(props){
    super(props)
    this.state={headlines:[],page:1}
    this.util=this.util.bind(this)
    this.handle=this.handle.bind(this)
    addres=this
  }
  componentDidMount(){
this.util(1) 
 }
util(flag){
  if(flag==1)
  this.setState((prev)=>{return {headlines:[],page:prev.page+1}});
  else
  this.setState((prev)=>{return {headlines:[],page:prev.page-1}});

  fetch(`https://newsapi.org/v2/everything?q=technology&page=${this.state.page}&pageSize=10&apiKey=9ed751fd08904a52a2ba4593563fe7d4`).then((res)=>res.json()).then((res)=>this.setState({headlines:res.articles,page:this.state.page}));

}
handle(te){
  let t=<text>{te}</text>
  ReactDOM.render(t,document.getElementById('root'));
}
  render(){
    return(
      <div>
        {this.state.headlines!=undefined &&
        this.state.headlines.map((news)=><><div className='headlines'><img className='newsimg' src={news.urlToImage}></img><div onClick={this.handle(news.content)} className='newstext'>{news.title}<br></br><br></br><br></br>source::{news.source.name}</div></div><br></br></>)}
      </div>
    )
  }
}

ReactDOM.render(<News/>,document.getElementById('root'))
