import React from "react";
import { responsive } from "./responsive.js";
export default class Side_bar extends React.Component{
    constructor(props){
        super(props)
        this.state={arr:this.props.arr,style:responsive.sidebar.main()}
    }
    render(){
        return(
            <div style={this.state.style}>
               {
                this.props.arr.map((ele)=><Element topic={ele}/>)
               }
            </div>
        )
    }
}
class Element extends React.Component{
    constructor(props){
        super(props)
        this.state={style:responsive.sidebar.Element()}
    }
    render(){
        return(
            <ul style={this.state.style}>
                {`${this.props.topic}`}
            </ul>
        )
    }
}
