import React from "react";
import { responsive } from "./responsive";
export default class Questions extends React.Component{
    constructor(props){
        super(props)
        let arr=responsive.question();
        this.state={index:0,parent:arr[0],q:arr[1],op1:arr[2],op2:arr[3],op3:arr[4],op4:arr[5]}
    }
    render(){
        return(
            <div style={this.state.parent}>
                <textarea style={this.state.q} value={this.props.q[this.state.index].question}></textarea>
                <input style={this.state.op1} type='checkbox' id="input_01"></input>
                <label htmlFor="input_01">{this.props.q[this.state.index].option1}</label>
                <input style={this.state.op2} type='checkbox' id="input_02"></input>
                <label htmlFor="input_02">{this.props.q[this.state.index].option2}</label>
                <input style={this.state.op3} type='checkbox' id="input_03"></input>
                <label htmlFor="input_03">{this.props.q[this.state.index].option3}</label>
                <input style={this.state.op4} type='checkbox' id="input_04"></input>
                <label htmlFor="input_04">{this.props.q[this.state.index].option4}</label>
                <button onClick={()=>this.setState((prev)=>{index:prev.index+1})}>Save and Continue</button>
            </div>
        )
    }
}
