import React from 'react'
import ReactDOM from 'react-dom'
import {res2} from './responsive_02.js'
import { Heading } from './heading.js'
import { current_user } from './index.js'
import img from './account.png'
import { Footer } from './footer.js'
export class Track_progress extends React.Component{
    constructor(){
        super()
        this.state={footer:res2.stats.footer()}
    }
    resize(){
        this.setState({})
    }
    render(){
        return(
            <>
            <Heading/>
            <Stats/>
            <Leaderboard/>
            <div style={this.state.footer}>
            <Footer/>
            </div>
            </>
        )
    }
}
class Stats extends React.Component{
    constructor(){
        super()
        this.state={info:[],style:res2.stats.header,image:res2.stats.image(img)}

    }
    render()
    {
        return<>
        <div style={this.state.image}>
        </div>
        <info style={{color:'white'}}>
            <div>Score:</div>
            <div>Accuracy:</div>
            <div>Rank</div>
        </info>
        </>
    }
}
class Leaderboard extends React.Component{
    constructor(){
        super()
        this.state={style:res2.stats.leaderboard(),arr:[],header:res2.stats.header(),heading:res2.stats.heading()}
        fetch('http://localhost:8000/track/getstats',{
            method:"POST",
            headers:{
                "Content-type":'application/json'
            },
            body:JSON.stringify(current_user)
        }).then((res)=>res.json()).then((res)=>{
            this.setState((prev)=>{return{arr:res,style:prev.style,header:prev.header,heading:prev.heading}})
        })
    }
    render(){
        return  <>
        <h1 style={this.state.heading}>Leaderboard</h1>
        <table style={this.state.style}> 
           <tr style={this.state.header}>
                  <th style={{color:'whitesmoke'}}>Rank</th>
                 <th style={{color:'whitesmoke'}}>User</th>
                 <th style={{color:'whitesmoke'}}>Score</th>
             </tr>
             {
                this.state.arr.map((info,ind)=>{
                    return<>
                    <tr >
                        <td style={{border:"1px solid grey",color:'whitesmoke',textAlign:'center'}}>{ind+1}</td>
                        <td style={{border:"1px solid grey",color:'white',textAlign:'center'}}>{info[0]}</td>
                        <td style={{border:"1px solid grey",color:"whitesmoke",textAlign:'center'}}>{info[1]}</td>
                    </tr>
                    </>
                })
             }
             </table>
             </>
    }
}



