import React from 'react'
import ReactDOM from 'react-dom'
//import responsive from './responsive.js'
import {Heading} from './heading.js'
import {Cpp_header} from './cpp.js'
import {Questions} from './questions.js'
import Add from './input.js'
import {Dsa_header} from './dsa.js'
import {Side_bar} from './side_bar.js'
import {Algorithms} from './algorithms.js'
import { Os_header } from './OS.js'
import img from './background.png'
document.body.style.backgroundColor='black'
document.body.style.backgroundImage=`url(${img})`
document.body.style.backgroundRepeat='no-repeat ,no-repeat'
class Main extends React.Component{
    constructor(props){
        super(props)
        this.func=this.func.bind(this)
         this.state={cpp_questions:[],main:true}
    }
    async func(){
        let questions=[]
       await fetch("http://localhost:8000/cpp/get").then((res)=>res=res.json()).then((res)=>questions=res)
       this.setState({cpp_questions:questions,main:false})
    }
    render(){
        return(
            <>
         <Heading/>
         {this.state.cpp_questions.length>0 && <>
            <Questions q={this.state.cpp_questions}/>
         </>}
         {this.state.main &&
         <>
        <Side_bar arr={['Data\nstructures','Cpp','Java','DBMS','Algorithms','OS']}/>
        <Cpp_header />
        <div style={{height:'60px'}}></div>
        <Dsa_header/>
        <div style={{height:'60px'}}></div>
        <Algorithms/>
        <Os_header/>
        </>
        }
        </>       
         )
    }
}
ReactDOM.render(<Main/>,document.getElementById('root'));
